package constant

const (
	TemplateStyle = `<style>img{width:180px}table{font-family:Arial,Helvetica,sans-serif}table{width:100%}ul{list-style-type:disc}table#content{width:100%}table#content tr td{padding:6px 0}table#content tr:nth-child(2){counter-reset:rowNumber}table#content tr.count{counter-increment:rowNumber}table#content tr.count td:first-child::before{content:counter(rowNumber)}table#content tr:last-child td::before{content:''}table#content tr:first-child td::before{content:''}.align-right{text-align:right}.align-left{text-align:left}.align-center{text-align:center}.border-y{border-top:1px solid #000;border-bottom:1px solid #000}.border-x{border-left:1px solid #000;border-right:1px solid #000}.border-l{border-left:1px solid #000}.border-r{border-right:1px solid #000}.border-bottom{border-bottom:1px solid #000}.border-top{border-top:1px solid #000}.pl{padding-left:12px!important}.pr{padding-right:12px!important}</style>`
	TemplateTable = `<table id="content" cellpadding="0" cellspacing="0"><tr class="align-center"><td class="border-y border-l"><b>NO</b></td><td class="border-y border-l"><b>DESCRIPTION</b></td><td class="border-y border-x"><b>TOTAL</b></td></tr>{{if .IsElectricityActive}}<tr class="count"><td class="align-center border-l"></td><td class="pl border-l">Abodemen Listrik</td><td class="align-right pr border-x">{{.ElectricitySubscription}}</td></tr><tr class="count"><td class="align-center border-l"></td><td class="pl border-l">Electricity Periode&nbsp;:&nbsp;{{.StartDate}}&nbsp;s/d&nbsp;{{.EndDate}}</td><td class="border-x"></td></tr><tr><td class="align-center border-l"></td><td class="border-l">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Meter Awal&nbsp;:&nbsp;{{.FirstElectricityMeter}}&nbsp;kWh </td><td class="border-x"></td></tr><tr><td class="align-center border-l"></td><td class="border-l">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Meter Akhir&nbsp;:&nbsp;{{.LastElectricityMeter}}&nbsp;kWh </td><td class="border-x"></td></tr><tr><td class="align-center border-l"></td><td class="border-l">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Pemakaian&nbsp;:&nbsp;{{.ElectricityUsage}}&nbsp;kWh&nbsp;x&nbsp;{{.ElectricityPricing}}</td><td class="align-right pr border-x">{{.ElectricityPrice}}</td></tr>{{end}}{{if .IsWaterActive}}<tr class="count"><td class="align-center border-l"></td><td class="pl border-l">Abodemen Air</td><td class="align-right pr border-x">{{.WaterSubscription}}</td></tr><tr class="count"><td class="align-center border-l"></td><td class="pl border-l">Water Periode&nbsp;:&nbsp;{{.StartDate}}&nbsp;s/d&nbsp;{{.EndDate}}</td><td class="border-x"></td></tr><tr><td class="align-center border-l"></td><td class="border-l">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Meter Awal&nbsp;:&nbsp;{{.FirstWaterMeter}}&nbsp;m<sup>3</sup></td><td class="border-x"></td></tr><tr><td class="align-center border-l"></td><td class="border-l">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Meter Akhir&nbsp;:&nbsp;{{.LastWaterMeter}}&nbsp;m<sup>3</sup></td><td class="border-x"></td></tr><tr><td class="align-center border-l"></td><td class="border-l">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Pemakaian&nbsp;:&nbsp;{{.WaterUsage}}&nbsp;m<sup>3</sup>&nbsp;x&nbsp;{{.WaterPricing}}</td><td class="align-right pr border-x">{{.WaterPrice}}</td></tr>{{end}}{{if .IsGasActive}}<tr class="count"><td class="align-center border-l"></td><td class="pl border-l">Abodemen Gas</td><td class="align-right pr border-x">{{.GasSubscription}}</td></tr><tr class="count"><td class="align-center border-l"></td><td class="pl border-l">Gas Periode&nbsp;:&nbsp;{{.StartDate}}&nbsp;s/d&nbsp;{{.EndDate}}</td><td class="border-x"></td></tr><tr><td class="align-center border-l"></td><td class="border-l">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Meter Awal&nbsp;:&nbsp;{{.FirstGasMeter}}&nbsp;m<sup>3</sup></td><td class="border-x"></td></tr><tr><td class="align-center border-l"></td><td class="border-l">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Meter Akhir&nbsp;:&nbsp;{{.LastGasMeter}}&nbsp;m<sup>3</sup></td><td class="border-x"></td></tr><tr><td class="align-center border-l"></td><td class="border-l">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Pemakaian&nbsp;:&nbsp;{{.GasUsage}}&nbsp;m<sup>3</sup>&nbsp;x&nbsp;{{.GasPricing}}</td><td class="align-right pr border-x">{{.GasPrice}}</td></tr>{{end}}{{if .IsAdministrationFeeActive}}<tr class="count"><td class="align-center border-l"></td><td class="pl border-l">Biaya Admin</td><td class="align-right pr border-x">{{.AdministrationFee}}</td></tr>{{end}}{{if .IsTaxActive}}<tr><td class="align-right pr border-top" colspan="2"><b>SUBTOTAL</b></td><td class="align-right pr border-top border-x"><b>{{.SubTotal}}</b></td></tr><tr><td class="align-right pr" colspan="2">{{.TaxName}}</td><td class="align-right pr border-x">{{.TaxPrice}}</td></tr><tr><td class="align-right pr" colspan="2"><b>TOTAL</b></td><td class="align-right pr border-x border-bottom"><b>{{.Total}}</b></td></tr>{{else}}<tr><td class="align-right pr border-top" colspan="2"><b>TOTAL</b></td><td class="align-right pr border-top border-x border-bottom"><b>{{.Total}}</b></td></tr>{{end}}</table>`
)
